11.query:
timeseries max(argocd_git_request_total), by: { repo }
| dedup repo
| summarize count()

13.query:
timeseries max(argocd_app_info), by: { name }
| dedup name
| summarize count()

19.query:
timeseries argocd_app_info = avg(argocd_app_info), by: { Name= name, Health = health_status, Server = dest_server, Namespace = dest_namespace, Project = project }, shift: -1m, interval: 1m
| fieldsAdd argocd_app_info = arrayLast(argocd_app_info)  | sort Name asc
| summarize count(), by:{Health}

24.query:
timeseries argocd_app_info = avg(argocd_app_info), by: { Name= name, Status = sync_status, Server = dest_server, Namespace = dest_namespace, Project = project }, shift: -1m, interval: 1m
| fieldsAdd argocd_app_info = arrayLast(argocd_app_info)  | sort Name asc
| summarize count(), by:{Status}
