processors.dqlScript:
/* there was a lack of "closed" status for PRs closed w/o merging */
parse pull_request, "JSON:record_json" 
| fieldsFlatten record_json, fields:{merged, state} 
| fieldsAdd if(merged == true, "merged", else: if(state == "closed", "closed", else: "open")), alias: event.status
| fieldsRemove record_json, merged, state

parse pull_request, "JSON:record_json" 
| fieldsFlatten record_json, fields:{merged_at, closed_at} 
//| fieldsAdd end_time = toTimestamp(merged_at)
| fieldsAdd if(merged == true, toTimestamp(merged_at), else: toTimestamp(closed_at)), alias: end_time
| fieldsRemove record_json, merged_at, closed_at

// 2024-11-24 
// adjustment od timestamp due to potetial PR events comming after source PR is beeing merged and closed. 
// Additionaly Slawek Z. need precise update time for sorting on dashboards
// A bug for 7200 sec gap? or something else....Toggle this one off
parse pull_request, "JSON:record_json" 
| fieldsFlatten record_json, fields:{updated_at} 
//| fieldsAdd dif2h = toLong(7200000000000)
| fieldsAdd nt = toLong(toTimestamp(now()))
//| fieldsAdd if(nt - toLong(toTimestamp(start_time)) < dif2h, start_time, else: toTimestamp(nt)), alias: st
//| fieldsAdd if(nt - toLong(toTimestamp(updated_at)) < dif2h, updated_at, else: toTimestamp(nt)), alias: timestamp
| fieldsAdd toTimestamp(nt), alias: timestamp
//| fieldsAdd if(nt - toLong(toTimestamp(end_time)) < dif2h, end_time, else: toTimestamp(nt)), alias: et
//| fieldsAdd if(event.status == "open", st, else: if(event.status == "merged", et, else: toTimestamp(nt))), alias: timestamp
//| fieldsRemove st, et, nt, dif2h
| fieldsRemove nt, dif2h, updated_at

parse repository, "JSON:record_json" 
| fieldsFlatten record_json, fields:{html_url} 
| fieldsRename vcs.repository.url.full = html_url

parse pull_request, "JSON:record_json" 
| fieldsFlatten record_json, fields:{labels}
| fieldsAdd ext.task.labels=toArray(labels)
//| expand ext.task.labels
| fieldsRemove record_json, labels

